/* Element selector — targets ALL paragraphs */
p {
  color: #333;
  line-height: 1.6;
}

/* Class selector — targets elements with class="highlight" */
.highlight {
  background-color: yellow;
  padding: 4px 8px;
}

/* ID selector — targets ONE specific element */
#main-title {
  font-size: 2.5rem;
  font-weight: bold;
}

/* Descendant selector — <a> inside .nav */
.nav a {
  text-decoration: none;
  color: #6366f1;
}

/* Pseudo-class — on hover */
.btn:hover {
  background-color: #4f46e5;
  transform: scale(1.05);
}

/* Pseudo-element — decorative before content */
.quote::before {
  content: '"';
  font-size: 3rem;
  color: #a78bfa;
}

/* Specificity: 0-0-1 (one element) */
p { color: black; }

/* Specificity: 0-1-0 (one class) — WINS over element */
.text { color: blue; }

/* Specificity: 1-0-0 (one ID) — WINS over class */
#intro { color: red; }

/* Specificity: 0-1-1 (one class + one element) */
p.text { color: green; }

/* For <p class="text" id="intro">, color = red (ID wins) */

/* Import Google Fonts */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');

/* Global typography setup */
body {
  font-family: 'Inter', system-ui, sans-serif;
  font-size: 16px;          /* Base size */
  line-height: 1.6;         /* Comfortable reading */
  color: #1e293b;           /* Dark slate text */
  background-color: #f8fafc; /* Light background */
}

h1 {
  font-size: 2.5rem;        /* 40px (relative to root) */
  font-weight: 700;
  letter-spacing: -0.025em; /* Tighter for headings */
  color: #0f172a;
}

.subtitle {
  font-size: 1.125rem;
  color: #64748b;           /* Muted gray */
  text-transform: uppercase;
  letter-spacing: 0.1em;    /* Wider for uppercase */
}

.link {
  color: #6366f1;           /* Indigo */
  text-decoration: none;
}

.link:hover {
  text-decoration: underline;
  text-underline-offset: 4px;
}

/* Different color formats — all produce similar purple */
.named    { color: purple; }
.hex      { color: #6366f1; }
.rgb      { color: rgb(99, 102, 241); }
.rgba     { color: rgba(99, 102, 241, 0.7); }  /* 70% opacity */
.hsl      { color: hsl(239, 84%, 67%); }

/* Gradients */
.gradient-bg {
  background: linear-gradient(135deg, #6366f1, #a855f7);
}

.rainbow {
  background: linear-gradient(to right, 
    #ef4444, #f59e0b, #10b981, #3b82f6, #8b5cf6);
}

.radial {
  background: radial-gradient(circle at top left, 
    #6366f1 0%, transparent 50%);
}

/* Gradient text effect */
.gradient-text {
  background: linear-gradient(to right, #6366f1, #ec4899);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

/* The universal fix — ALWAYS include this */
*, *::before, *::after {
  box-sizing: border-box;
}

.card {
  width: 300px;           /* Total width = 300px (thanks to border-box) */
  padding: 24px;          /* Inner spacing */
  border: 2px solid #e2e8f0;
  border-radius: 12px;    /* Rounded corners */
  margin: 16px;           /* Outer spacing */
  background: white;
}

/* Margin shorthand: top right bottom left (clockwise) */
.element {
  margin: 10px 20px 10px 20px; /* All four sides */
  margin: 10px 20px;           /* top/bottom  left/right */
  margin: 10px;                /* All sides equal */
}

/* Center a block element horizontally */
.container {
  max-width: 800px;
  margin: 0 auto;   /* 0 top/bottom, auto left/right = centered */
}

/* Block: full width, stacks vertically */
.block-element {
  display: block;
  width: 200px;       /* width works */
  margin: 10px auto;  /* centering works */
}

/* Inline: flows with text, ignores width/height */
.inline-element {
  display: inline;
  /* width: 200px;  ← IGNORED for inline! */
  padding: 4px 8px;   /* horizontal padding works, vertical may overflow */
}

/* Inline-block: best of both worlds */
.badge {
  display: inline-block;
  width: 80px;        /* width works! */
  padding: 4px 12px;
  text-align: center;
  border-radius: 20px;
  background: #6366f1;
  color: white;
}

/* Relative: moves from its normal position */
.tooltip-trigger {
  position: relative;   /* Makes this the reference point */
}

/* Absolute: positioned relative to nearest positioned ancestor */
.tooltip {
  position: absolute;
  bottom: 100%;         /* Above the trigger */
  left: 50%;
  transform: translateX(-50%); /* Center horizontally */
  background: #1e293b;
  color: white;
  padding: 8px 12px;
  border-radius: 6px;
  z-index: 10;          /* Appear above other elements */
}

/* Fixed: stays in place when scrolling */
.sticky-header {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 100;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
}

/* Sticky: normal until scroll, then sticks */
.table-header {
  position: sticky;
  top: 0;
  background: white;
}

/* The magic of centering — just 3 lines! */
.center-everything {
  display: flex;
  justify-content: center;  /* Horizontal center */
  align-items: center;      /* Vertical center */
  min-height: 100vh;        /* Full viewport height */
}

/* Navigation bar */
.navbar {
  display: flex;
  justify-content: space-between; /* Logo left, links right */
  align-items: center;
  padding: 16px 24px;
  gap: 16px;
}

/* Card grid that wraps */
.card-grid {
  display: flex;
  flex-wrap: wrap;          /* Allow wrapping */
  gap: 24px;                /* Consistent spacing */
}

.card {
  flex: 1 1 300px;          /* Grow, shrink, min 300px wide */
  /* This creates a responsive grid without media queries! */
}

/* Sticky footer (footer always at bottom) */
.page {
  display: flex;
  flex-direction: column;
  min-height: 100vh;
}
.page-content {
  flex: 1;    /* Grows to fill all available space */
}
.page-footer {
  /* Naturally pushed to the bottom */
}

/* Equal-height columns */
.columns {
  display: flex;
  gap: 24px;
}
.column {
  flex: 1;    /* All columns equal width */
  /* All columns are automatically equal height! */
}

/* Right-align the last item (e.g., logout button) */
.nav-items {
  display: flex;
  align-items: center;
  gap: 12px;
}
.nav-items .logout {
  margin-left: auto;  /* Pushes to the right */
}

/* Basic 3-column grid */
.grid-basic {
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;  /* left - center(2x) - right */
  gap: 24px;
}

/* Responsive auto-fit grid (no media queries!) */
.card-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 24px;
}
/* Creates 1 column on mobile, 2 on tablet, 3+ on desktop — automatically! */

/* Full page layout using Grid Areas */
.page-layout {
  display: grid;
  grid-template-areas:
    "header  header"
    "sidebar main  "
    "footer  footer";
  grid-template-columns: 250px 1fr;
  grid-template-rows: 60px 1fr 50px;
  min-height: 100vh;
}

.header  { grid-area: header; }
.sidebar { grid-area: sidebar; }
.main    { grid-area: main; }
.footer  { grid-area: footer; }

/* Image gallery with featured item */
.gallery {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 16px;
}

/* Featured image spans 2 columns and 2 rows */
.gallery .featured {
  grid-column: span 2;
  grid-row: span 2;
}

/* Center content in a grid cell */
.centered-grid {
  display: grid;
  place-items: center;  /* Shorthand for align + justify */
  min-height: 300px;
}

/* Mobile first: base styles for small screens */
.container {
  padding: 16px;
  max-width: 1200px;
  margin: 0 auto;
}

.grid {
  display: grid;
  gap: 16px;
  grid-template-columns: 1fr;  /* Single column on mobile */
}

/* Tablet: 768px and up */
@media (min-width: 768px) {
  .grid {
    grid-template-columns: repeat(2, 1fr);  /* 2 columns */
    gap: 24px;
  }
}

/* Desktop: 1024px and up */
@media (min-width: 1024px) {
  .grid {
    grid-template-columns: repeat(3, 1fr);  /* 3 columns */
    gap: 32px;
  }
}

/* Fluid typography — scales with viewport */
h1 {
  font-size: clamp(1.75rem, 4vw, 3rem);
  /* Min: 28px, Preferred: 4% of viewport, Max: 48px */
}

/* Smooth hover effect on a button */
.btn {
  padding: 12px 24px;
  background: #6366f1;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;

  /* Transition: animate ALL changed properties over 0.3s */
  transition: all 0.3s ease;
}

.btn:hover {
  background: #4f46e5;
  transform: translateY(-2px);  /* Float up */
  box-shadow: 0 10px 25px rgba(99, 102, 241, 0.3);
}

.btn:active {
  transform: translateY(0) scale(0.98);  /* Press down */
}

/* Card hover effect */
.card {
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.card:hover {
  transform: translateY(-8px);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
}

/* Loading spinner */
@keyframes spin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.spinner {
  width: 40px;
  height: 40px;
  border: 4px solid #e2e8f0;
  border-top-color: #6366f1;
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
}

/* Fade-in-up entrance animation */
@keyframes fadeInUp {
  0% {
    opacity: 0;
    transform: translateY(30px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-in {
  animation: fadeInUp 0.6s ease forwards;
}

/* Respect user motion preferences */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    transition-duration: 0.01ms !important;
  }
}

/* Define variables globally */
:root {
  --color-primary: #6366f1;
  --color-primary-hover: #4f46e5;
  --color-bg: #ffffff;
  --color-text: #1e293b;
  --color-surface: #f8fafc;
  --radius: 8px;
  --shadow: 0 4px 12px rgba(0, 0, 0, 0.1);
  --font-sans: 'Inter', system-ui, sans-serif;
}

/* Dark mode — just override the variables! */
.dark {
  --color-bg: #0a0a0f;
  --color-text: #f1f5f9;
  --color-surface: #1e1b2e;
  --shadow: 0 4px 12px rgba(0, 0, 0, 0.4);
}

/* Use variables throughout your CSS */
body {
  font-family: var(--font-sans);
  background: var(--color-bg);
  color: var(--color-text);
}

.card {
  background: var(--color-surface);
  border-radius: var(--radius);
  box-shadow: var(--shadow);
}

.btn-primary {
  background: var(--color-primary);
}

.btn-primary:hover {
  background: var(--color-primary-hover);
}

/* calc() — math in CSS */
.sidebar-layout {
  display: grid;
  grid-template-columns: 250px calc(100% - 250px - 24px);
  gap: 24px;
}

/* clamp() — responsive without media queries */
h1 { font-size: clamp(1.5rem, 4vw, 3rem); }
.container { width: clamp(320px, 90%, 1200px); }

/* Native CSS Nesting */
.card {
  padding: 24px;

  .title {
    font-size: 1.25rem;
    font-weight: 600;
  }

  &:hover {
    transform: translateY(-4px);
  }
}

/* :has() — parent selector (game-changer!) */
.card:has(img) {
  padding-top: 0; /* Remove top padding if card has an image */
}

.form-group:has(input:invalid) {
  border-color: red; /* Highlight group if input is invalid */
}

/* aspect-ratio */
.video-container {
  aspect-ratio: 16 / 9;
  width: 100%;
}
